import React, { Component } from 'react';
import Directory from './DirectoryComponent';
import CampsiteInfo from './CampsiteInfoComponent';
import Header from './HeaderComponent';
import Footer from './FooterComponent';
import Home from './HomeComponent';
import Contact from './ContactComponent';
import { Switch, Route, Redirect } from 'react-router-dom';
import { COMMENTS } from '../shared/campsites';
import { CAMPSITES } from '../shared/campsites';
import { PARTNERS } from '../shared/campsites';
import { PROMOTIONS } from '../shared/campsites';

class Main extends Component {
    constructor( props ) {
        super( props );
        this.state = {
            campsites: CAMPSITES,
            comments: COMMENTS,
            partners: PARTNERS,
            promotions: PROMOTIONS

        };
    }

  render() {

    const HomePage = () => {
        return (
            <Home 
                campsite={ this.state.campsites.filter( campsite => campsite.featured )[0] }
                promotion={ this.state.promotions.filter( promotion.featured )[0] }
                partner={ this.state.partners.filters( ) }
            />
        );
    }

    return (
        <div>
            <Header />
            <Switch>
                <Route path='/home' component={ HomePage } />
                <Route exact path ='/directory' render={() => <Directory campsites={ this.state.campsites } />} />
                <Route exact path='/contactus' component={ Contact } />
                <Redirect to='/home' />
            </Switch>
            <Footer />
          </div>
    );
  }
}

export default Main;
